// searchbox autocomplete
function initSearchForms() {
	jQuery('form.search-form').autoCompleteForm({
		resultsHolder: 'div.autocomplete',
		inputField: 'textarea',
		alwaysRefresh: false
	});
}

// swipe items
function initSwipeRows() {
	var checkedClass = 'checked';
	var activeClass = 'slided';/*Hala:I'm commenting this as we don't need to swipe the elements ,favorites will be done by something else*/
	var swipeClass = 'swipe-active';
	jQuery('.items-list').each(function() {
		var list = jQuery(this);
		var rows = list.children();
		rows.each(function() {
			var row = jQuery(this);
			var opener = row.find('a.opener');
			var checkBtn = row.find('a.fav-holder');
			var swipeHandler = new Hammer.Manager(opener[0]);
			var clickHandler = function(e) {
				e.preventDefault();
				row.toggleClass(checkedClass);
			};
			checkBtn.on('click', clickHandler);
			swipeHandler.add(new Hammer.Pan({
				direction: Hammer.DIRECTION_HORIZONTAL,
				threshold: 5
			}));
			swipeHandler.add(new Hammer.Tap({ event: 'doubletap', taps: 2 }));
			swipeHandler.add(new Hammer.Tap({ event: 'singletap' }));
			swipeHandler.get('doubletap').recognizeWith('singletap');
			swipeHandler.get('singletap').requireFailure('doubletap');
			swipeHandler.on('singletap doubletap', function(ev) {
				opener.trigger('customclick');
			}).on('panstart', function(e) {
					list.addClass(swipeClass);
				}).on('panend', function(e) {
				if (e.deltaX < 0) {
					rows.removeClass(activeClass);
					row.addClass(activeClass);
				} else {
					row.removeClass(activeClass);
				}
				list.removeClass(swipeClass);
			});
		});
	});
}

